import React,{useState} from 'react'
import './form.css'
const form = (props) => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [input, settInput]=useState('');

    const getInputData=(e)=>{
        // console.log(e.target.value)
        settInput(e.target.value);
    }
    const submitData=(e)=>{
        e.preventDefault();
        const newtodo={
            id:props.demotodos.length+1,
            todo:input
        }
        props.demotodos.push(newtodo);
        console.log(props.demotodos);
        props.addtodo(props.demotodos);
    }

  return (
    <div className='form'>
        <form onSubmit={submitData}>
            <input onChange={getInputData} type='text' placeholder='Add todo'></input>
        </form>
    </div>
  )
}

export default form
