import React,{useState} from 'react'
import TodoList from './TodoList'
import Form from './Form';

const TodoApp = () => {

    const DUMMY_TODOS = [
        {
            id: 0,
            todo: "Go to Shopping",
            checked:true
        },
        {
            id: 1,
            todo: "Buy Groceries",
            checked:false
        },
        {
            id: 2,
            todo: "Watch Movies",
            checked:true
        },
        {
            id: 3,
            todo: "Learn React",
            checked:false
        }
    ];

    const [todos, setTodos] = useState(DUMMY_TODOS);

    const addTodo = (todo) => {
        setTodos([...todos, todo]);
    }
    const deletetodo=(id)=>{
        setTodos((prevtodos)=>{
            return prevtodos.filter((item)=>item.id!==id);
        })
    }

    const checkedChange=(id)=>{
        setTodos((prevtodos)=>{
            return prevtodos.map((item)=>item.id==id ? !item.checked : "")
        })
    }
    return (
        <div style={{width:'360px',margin:'30px auto'}}>
            <h1 style={{textAlign:'center'}}>Todo App</h1>
            <Form addTodo={addTodo} todos={todos} />
            <TodoList todos={todos} deletetodo={deletetodo} change/>

        </div>
    )
}

export default TodoApp
