import React,{useState} from 'react'
import TodoList from './components/todoList'
import Form from './components/form'
import './App.css'
const App = () => {
  let demotodos=[
    {
      id:1,
      todo:"Coding"
    },
    {
      id:2,
      todo:"Learn React"
    },
    {
      id:3,
      todo:"Orientation program"
    },
    {
      id:4,
      todo:"Painting"
    },
    {
      id:5,
      todo:"Shopping"
    }
  ]

  const [todos,setTodos]=useState(demotodos);


  const addtodo=(newtodos)=>{
    console.log("Added Successfully")
    setTodos([...todos,newtodo]);
    console.log(todos);
  }

  return (
    <div className='mainbox'>
      <h1>Todo-List</h1>
      <Form demotodos={demotodos} addtodo={addtodo} />
      <TodoList demotodos={demotodos} />
    </div>
  )
}

export default App
